const experience = [
  {
    id: 1,
    date: "Oct. 2023 - Sep. 2023",
    position: "QA Engineer Intern",
    company: "Jabra",
    url: "http://www.jabra.com/",
    description: [
      {
        id: 1,
        description:
          "Coordinated the release of iOS and Android SDK versions for a specific headset product",
      },
      {
        id: 2,
        description:
          "Assisted in developing the frontend interface for a demo app which focused on customizing product firmware",
      },
      {
        id: 3,
        description:
          "Enhanced BlueParrott reliability and customer satisfaction by optimizing firmware QA workflows through JIRA and Confluence integration",
      },
      {
        id: 4,
        description:
          "Build iOS and Android test pipelines, expanding support for public repo development in the CI/CD process",
      },
    ],
  },
  {
    id: 2,
    date: "Jun. 2023 - Sep. 2023",
    position: "Software Engineer Intern",
    company: "1 Thing Org",
    url: "https://www.1thing.org/",
    description: [
      {
        id: 1,
        description:
          "Developed a mobile application using TypeScript and React Native for efficient event scheduling, with reusable components for streamlined development",
      },
      {
        id: 2,
        description:
          "Employed React Hooks (useState and useEffect) to enhance code cleanliness and maintainability in components, and implemented centralized state management with Redux",
      },
      {
        id: 3,
        description:
          "Utilized FireStore and Cloud Firebase to create schema and persistent data storage",
      },
      {
        id: 4,
        description:
          "Adopted OAuth 2.0 for third-party login (Google, Facebook, Twitter) to enhance the authentication and authorization security, and employed JSON Web Token for secure data transmission",
      },
      {
        id: 5,
        description:
          "Integrated Google Map API to provide real-time location identification, enabling users to publish new activities within the app with accurate geolocation data",
      },
    ],
  },
  {
    id: 3,
    date: "Jul. 2021 - Nov. 2021",
    position: "Research Assistant",
    company: "Jiangsu University (UJS)",
    url: "http://www.ujs.edu.cn/",
    description: [
      {
        id: 1,
        description:
          "Developed customer churn analysis evaluation indices and utilized Python web crawlers to collect dataset of 600+ records from shopping platforms for analysis",
      },
      {
        id: 2,
        description:
          "Reduced data redundancy by 12% through correlation analysis for feature selection and subset extraction",
      },
      {
        id: 3,
        description:
          "Achieved a 95% overall accuracy by building classification models with BP neural networks, XG-Boost decision trees, and SVM support vector machines",
      },
    ],
  },
];

export default experience;
